function bs(arr, val) {
    let minIdx = 0,
        maxIdx = arr.length - 1,
        guessIdx;
    while (minIdx < maxIdx) {
        guessIdx = Math.floor((minIdx + maxIdx) / 2);
        if (arr[guessIdx] === val) {
            return guessIdx;
        } else if (arr[minIdx] < arr[guessIdx]) {
            minIdx = guessIdx + 1;
        } else {
            maxIdx = guessIdx - 1;
        }
    }
    return -1;
}

