function fib(idx) {
    switch (idx) {
        case 0:
            return 0;
        case 1:
            return 1;
        default:
            return fib(idx - 1) + fib(idx - 2);
    }
}

(0, _ava.test)('fibonacci 5-th index equal 5', t => t.is(fib(5), 5));
