function debounce(fn, delay) {
    let last = {
        time: null,
        timerId: null
    };
    return () => {
        let time = Date.now();
        if (last.time && last.timerId < delay) {
            clearTimeout(last.timerId);
        }
        last = {
            time,
            timerId: setTimeout(fn, delay)
        };
    };
}

