function bs(arr, val) {
    let minIdx = 0;
    let maxIdx = arr.length - 1;
    let guess;
    while (minIdx <= maxIdx) {
        guess = Math.floor((minIdx + maxIdx) / 2);
        if (arr[guess] === val) {
            return guess;
        } else if (arr[guess] < val) {
            minIdx = guess + 1;
        } else {
            maxIdx = guess - 1;
        }
    }
    return -1;
}

