function intersect(arr1, arr2) {
    let seen = arr1.reduce((seen, current) => {
        seen[current] = true;
        return seen;
    }, {});
    return arr2.reduce((result, current) => {
        if (seen[current]) {
            result.push(current);
        }
        return result;
    }, []);
}

