function debounce(fn, delay = 0) {
    let last = {
        time: null,
        timerId: null
    };
    return () => {
        let time = Date.now();
        if (last.time && time - last.time < delay) {
            clearTimeout(last.timerId);
        }
        last = {
            time,
            timerId: setTimeout(fn, delay)
        };
    };
}
