let reduceAsync = async (array, fn, val) => {
    for (let a of array) {
        val = fn(val, (await a()));
    }
    return val;
};

(0, _ava.test)(async t => {
    let a = () => Promise.resolve('a');
    let b = () => Promise.resolve('b');
    let c = () => new Promise(resolve => {
        setTimeout(() => {
            resolve('c');
        }, 100);
    });

    t.deepEqual((await reduceAsync([a, b, c], (acc, current) => [...acc, current], [])), ['a', 'b', 'c']);
    t.deepEqual((await reduceAsync([a, b, c], (acc, current) => [...acc, current], ['d'])), ['d', 'a', 'b', 'c']);
});

