"use strict";

// суть в том чтобы в цикле пройтись по массиву
// внутри ввести переменные next и  current

function isSorted(arr) {
    for (let i = 0; i < arr.length; i++) {
        let next = arr[i + 1];
        let current = arr[i];
        if (next && current >= next) {
            return false;
        }
    }
    return true;
}
