function bs(arr) {
    for (let i = arr.length - 1; i > 1; i--) {
        for (let j = 0; j < 1; j++) {
            if (arr[j] > arr[j + 1]) {
                let temp = arr[j];
                arr[j] = arr[j + 1];
                arr[j + 1] = temp;
            }
        }
    }
    return arr;
}

